import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [produits, setProduits] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:3001/produits')
      .then(response => setProduits(response.data))
      .catch(error => console.error('Erreur:', error));
  }, []);

  return (
    <div className="container">
      <h1>Liste des Produits (Re√ßus de App1)</h1>
      <table className="product-table">
        <thead>
          <tr>
            <th>Image</th>
            <th>Nom</th>
            <th>Stock</th>
            <th>Prix</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {produits.map(produit => (
            <tr key={produit.id}>
              <td><img src={produit.image} alt={produit.nom} className="product-img" /></td>
              <td>{produit.nom}</td>
              <td>{produit.stock}</td>
              <td className="price">${produit.prix}</td>
              <td>
                <span className={produit.stock > 0 ? "in-stock" : "out-stock"}>
                  {produit.stock > 0 ? "In-Stock" : "Out-Stock"}
                </span>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
