import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [produits, setProduits] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:3001/produits')
      .then(response => setProduits(response.data))
      .catch(error => console.error('Erreur:', error));
  }, []);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold text-blue-600 mb-6">
        ðŸ›’ Liste des Produits (ReÃ§us de App1)
      </h1>

      <div className="w-full max-w-4xl bg-white shadow-md rounded-lg p-6">
        <table className="w-full border-collapse border border-gray-300">
          <thead>
            <tr className="bg-blue-500 text-white">
              <th className="border border-gray-300 px-4 py-2">ID</th>
              <th className="border border-gray-300 px-4 py-2">Nom</th>
              <th className="border border-gray-300 px-4 py-2">Prix (â‚¬)</th>
            </tr>
          </thead>
          <tbody>
            {produits.length > 0 ? (
              produits.map((produit) => (
                <tr key={produit.id} className="hover:bg-gray-100">
                  <td className="border border-gray-300 px-4 py-2 text-center">{produit.id}</td>
                  <td className="border border-gray-300 px-4 py-2">{produit.nom}</td>
                  <td className="border border-gray-300 px-4 py-2 text-center font-semibold text-green-600">{produit.prix} â‚¬</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="3" className="text-center text-gray-500 py-4">
                  Aucun produit disponible
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
