import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [produits, setProduits] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:3001/produits')
      .then(response => setProduits(response.data))
      .catch(error => console.error('Erreur:', error));
  }, []);

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-6">
      <div className="w-full max-w-4xl bg-white shadow-lg rounded-lg p-6">
        <h1 className="text-2xl font-bold text-center text-gray-800 mb-4">
          ðŸ“¦ Liste des Produits (ReÃ§us de App1)
        </h1>
        <table className="w-full border-collapse">
          <thead>
            <tr className="bg-gray-200 text-gray-700">
              <th className="p-3 text-left">Image</th>
              <th className="p-3 text-left">Nom</th>
              <th className="p-3 text-left">Prix</th>
              <th className="p-3 text-left">Stock</th>
              <th className="p-3 text-left">Statut</th>
            </tr>
          </thead>
          <tbody>
            {produits.map((produit) => (
              <tr key={produit.id} className="border-b hover:bg-gray-100">
                <td className="p-3">
                  <img src={produit.image} alt={produit.nom} className="h-12 w-12 object-cover rounded-md" />
                </td>
                <td className="p-3 font-semibold text-gray-900">{produit.nom}</td>
                <td className="p-3 font-bold text-gray-800">${produit.prix}</td>
                <td className="p-3 text-gray-700">{produit.stock}</td>
                <td className="p-3">
                  {produit.stock > 0 ? (
                    <span className="px-2 py-1 bg-green-100 text-green-800 text-sm rounded">En Stock</span>
                  ) : (
                    <span className="px-2 py-1 bg-red-100 text-red-800 text-sm rounded">Rupture</span>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
