import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [produits, setProduits] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:3001/produits')
      .then(response => setProduits(response.data))
      .catch(error => console.error('Erreur:', error));
  }, []);

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-blue-400 to-purple-500 p-6">
      <div className="w-full max-w-4xl bg-white shadow-lg rounded-lg p-8">
        <h1 className="text-3xl font-bold text-blue-600 text-center mb-6">
          ðŸ›’ Liste des Produits (ReÃ§us de App1)
        </h1>

        <div className="overflow-x-auto">
          <table className="w-full border-collapse border border-gray-300 rounded-lg shadow-md">
            <thead>
              <tr className="bg-blue-600 text-white">
                <th className="border border-gray-300 px-6 py-3 text-lg">ID</th>
                <th className="border border-gray-300 px-6 py-3 text-lg">Nom</th>
                <th className="border border-gray-300 px-6 py-3 text-lg">Prix (â‚¬)</th>
              </tr>
            </thead>
            <tbody>
              {produits.length > 0 ? (
                produits.map((produit) => (
                  <tr key={produit.id} className="hover:bg-gray-100 transition-all">
                    <td className="border border-gray-300 px-6 py-4 text-center text-gray-700">{produit.id}</td>
                    <td className="border border-gray-300 px-6 py-4 text-gray-800">{produit.nom}</td>
                    <td className="border border-gray-300 px-6 py-4 text-center font-semibold text-green-600">{produit.prix} â‚¬</td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="3" className="text-center text-gray-500 py-6 text-lg">
                    Aucun produit disponible
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default App;
